"""Unit tests for cerrormodels module.

"""
import unittest
from netsquid.components.models.cerrormodels import ClassicalErrorModel
from netsquid.qubits.qubit import Qubit


class BasicTestErrorModel(ClassicalErrorModel):
    """Basic test model."""

    def __init__(self, **kwargs):
        super().__init__()

    def error_operation(self, items, increment=1, **kwargs):
        for idx in range(len(items)):
            if items[idx] is not None:
                items[idx] += increment
                if "multiplier" in kwargs:
                    items[idx] *= kwargs["multiplier"]


class TestModel(unittest.TestCase):
    """Unit tests for the ClassicalErrorModel class."""

    def test_items_is_list_no_qubits(self):
        """Test items should be a list without any `Qubit`s."""
        model_1 = BasicTestErrorModel()
        with self.assertRaises(TypeError):
            model_1(items=1)
        with self.assertRaises(TypeError):
            model_1(items="test_input_string")
        with self.assertRaises(TypeError):
            model_1(items=(1, 2, 3))
        with self.assertRaises(TypeError):
            model_1(items=[1, 2, Qubit(), 4])

    def test_error_operation(self):
        """Test to check if arguments and kwargs are used correctly"""
        model_1 = BasicTestErrorModel()
        items = [None, 2, 3]
        model_1(items=items, multiplier=3)
        self.assertListEqual(items, [None, 9, 12])
        model_1(items=items, increment=0, multiplier=2)
        self.assertListEqual(items, [None, 18, 24])


if __name__ == "__main__":
    unittest.main()
